CREATE FUNCTION public.fr_cover_template(p_organisation character varying, p_policy_type character varying, p_policy_type_status character varying, p_cover_type character varying, p_cover_name character varying) RETURNS TABLE(organisation character varying, policy_type character varying, policy_type_status text, cover_type character varying, cover_name character varying, mandatory character varying, input_type character varying, list_of_options character varying, display_order integer, cover_description text, separator character varying, cover_template_id integer, policy_type_lid integer, cover_type_lid integer)
    LANGUAGE sql
    AS $$
select 
	orga.name as organisation,
	ldpta.value as policy_type, 
	CASE
        ldpta.status
        WHEN 0 THEN 'Inactive' :: text
        ELSE 'Active' :: text
    END AS policy_type_status,
	ldct.value as cover_type,
	mct.cover_name as cover_name, 
	mct.mandatory as mandatory, 
	mct.input_type as input_type, 
	input_lov as list_of_options,
	display_sequence as display_order,
	mct.cover_description as cover_description,
    ' -- ' as separator,
    mct.id as cover_template_id,
    ldpta.id as policy_type_lid,
    ldct.id as cover_type_lid
from mstr_cover_template mct
	inner join lookup_data ldpt
		on mct.policy_type_id = ldpt.id
    inner join organisation org
		on org.id = mct.organization_id
	inner join lookup_data ldct
		on mct.cover_type = ldct.id
    right join lookup_data ldpta
        on mct.policy_type_id = ldpta.id
	inner join organisation orga
		on orga.id = ldpta.organisation_id
where 1 = 1
and ldpta.lookup_name = 'POLICY_TYPE'
    and orga.name ilike coalesce(p_organisation, orga.name)
    and ldpta.value ilike coalesce(p_policy_type, ldpta.value)
    and (CASE ldpta.status WHEN 0 THEN 'Inactive' :: text ELSE 'Active' :: text END) ilike coalesce(p_policy_type_status, CASE ldpta.status WHEN 0 THEN 'Inactive' :: text ELSE 'Active' :: text END)
    and (ldct.value ilike coalesce(p_cover_type, ldct.value) or p_cover_type is null)
    and (mct.cover_name ilike coalesce(p_cover_name, mct.cover_name) or p_cover_name is null)
order by 
    org.name ASC NULLS LAST, orga.name, ldpta.value, mct.display_sequence
$$;


