    LANGUAGE sql
    AS $$
select 
    distinct 
    TRIM(BOTH FROM (usr.first_name::text || ' '::text) || usr.last_name::text) AS user_name,
    usr.email_id as user_email, 
    aclcat.name AS permitted_function_name,
