    and aclcat.name ilike coalesce(p_permitted_function_name, aclcat.name)
    and aclact.name ilike coalesce(p_permitted_action_name, aclact.name)
group by
    TRIM(BOTH FROM (usr.first_name::text || ' '::text) || usr.last_name::text),
    usr.email_id, 
    aclcat.name,
    aclact.name,
    usr.id,
