CREATE PROCEDURE public.proc_all_table_user_update_status()
    LANGUAGE plpgsql
    AS $$
DECLARE
    tbl RECORD;
    col TEXT;
    col_data_type TEXT;
    cnt INTEGER;
    issue_ids TEXT;
    qry TEXT;
    update_qry TEXT;
    recheck_qry TEXT;
    final_status TEXT;
    remarks TEXT;
BEGIN
    FOR tbl IN
        SELECT * FROM (VALUES
            ('address'),
            ('employee'),
            ('company'),
            ('company_detail'),
            ('company_strategy'),
            ('file_uploads'),
            ('contact'),
            ('contact_communication_details'),
            ('insurer'),
            ('tpa'),
            ('opportunity'),
            ('policy')
        ) AS t(table_name)
    LOOP
        FOREACH col IN ARRAY ARRAY['created_by', 'updated_by']
        LOOP
            -- Check if the column exists and get its type
            SELECT data_type INTO col_data_type
            FROM information_schema.columns
            WHERE table_schema = 'public'
              AND table_name = tbl.table_name
              AND column_name = col;

            IF col_data_type IS NULL THEN
                INSERT INTO all_table_user_update_status(table_name, column_name, status, remarks)
                VALUES ('public.' || tbl.table_name, col, 'SKIPPED - NO COLUMN', 'Column not present');
                CONTINUE;
            END IF;

            -- Skip non-integer type columns
            IF col_data_type NOT IN ('integer', 'bigint', 'smallint') THEN
                INSERT INTO all_table_user_update_status(table_name, column_name, status, remarks)
                VALUES ('public.' || tbl.table_name, col, 'SKIPPED - TYPE MISMATCH', 'Data type: ' || col_data_type);
                CONTINUE;
            END IF;

            -- Get list of unmatched IDs
            qry := format(
                'SELECT string_agg(DISTINCT %I::TEXT, '',''), count(DISTINCT %I) FROM public.%I WHERE %I IS NOT NULL AND %I NOT IN (SELECT id FROM users)',
                col, col, tbl.table_name, col, col);
            EXECUTE qry INTO issue_ids, cnt;

            IF cnt > 0 THEN
                -- Perform update
                update_qry := format('UPDATE public.%I AS c SET %I = u.mig_user_id FROM zz_load_mig_user_mapping u WHERE c.%I = u.id',
                                     tbl.table_name, col, col);
                EXECUTE update_qry;

                -- Re-check
                recheck_qry := format('SELECT count(*) FROM public.%I WHERE %I IS NOT NULL AND %I NOT IN (SELECT id FROM users)',
                                      tbl.table_name, col, col);
                EXECUTE recheck_qry INTO cnt;

                final_status := CASE WHEN cnt = 0 THEN 'SUCCESS' ELSE 'FAILED' END;
                remarks := CASE WHEN cnt = 0 THEN 'All IDs updated'
                                ELSE 'Some IDs still unmatched after update'
                           END;

                INSERT INTO all_table_user_update_status(table_name, column_name, status, issue_count, issue_ids, remarks)
                VALUES ('public.' || tbl.table_name, col, final_status, cnt, issue_ids, remarks);
            ELSE
                INSERT INTO all_table_user_update_status(table_name, column_name, status, issue_count, issue_ids, remarks)
                VALUES ('public.' || tbl.table_name, col, 'NO ACTION NEEDED', 0, NULL, 'All IDs valid');
            END IF;
        END LOOP;
    END LOOP;
END;
$$;


