CREATE FUNCTION public.ufm_company_biz_done_by_date_range(user_id integer, team integer, start_date timestamp without time zone, end_date timestamp without time zone) RETURNS TABLE(total_gross_premium numeric, total_commission_amount numeric, total_terrorism_amount numeric, total_net_premium numeric)
    LANGUAGE plpgsql
    AS $$
DECLARE
    user_ids INT[];
BEGIN
    IF team = 0 THEN
        user_ids := ARRAY[user_id];
    ELSE
        SELECT ARRAY(
            SELECT id
            FROM (
                WITH RECURSIVE HierarchyCTE AS (
                    SELECT id, reporting_user_id
                    FROM users
                    WHERE id = user_id

                    UNION ALL

                    SELECT u.id, u.reporting_user_id
                    FROM users u
                    INNER JOIN HierarchyCTE h ON u.reporting_user_id = h.id
                )
                SELECT id FROM HierarchyCTE
            ) AS subquery
        ) INTO user_ids;
    END IF;

    RETURN QUERY

    SELECT SUM(grossPremium), SUM(commissionAmount), SUM(terrorismCommissionAmount), SUM(netPremium) from (
    SELECT 
        SUM(policy.gross_premium_amount) AS grossPremium,
        SUM(policy.brokerage_amount) AS commissionAmount,
        SUM(policy.commission_terrorism) AS terrorismCommissionAmount,
        SUM(policy.net_premium_amount) AS netPremium
    FROM policy
    INNER JOIN company 
        ON company.id = policy.company_id 
        AND company.deleted_at IS NULL
    WHERE 
        policy.date_of_income BETWEEN start_date AND end_date
        AND policy.created_by = ANY(user_ids)
    GROUP BY 
        policy.company_id, company.company_name) AS company_summary;
END;
$$;


