def mk_inp():
    return Input(
        id="msg",
        placeholder="Type a message...",
        autofocus=True,
        style=f"width: 60%; max-width: 600px; padding: {get_spacing('sm')}; font-size: {get_typography('md')}; border-radius: {get_border_radius('sm')}",
    )
